import Board from "../../../src/components/Board.vue";

describe("Cell", () => {
  it("is initially 'active'", () => {
    cy.visit("/");
    cy.get(".cell-container").should("have.class", "active");
  });

  describe("Mine Cell click", () => {
    it("reveals a mine", () => {
      cy.resetBoard("1", "2", "1");
      cy.get(".cell-container")
        .first()
        .click();
      cy.get(".cell-container .mine");
    });
  });

  describe("SafeCell click", () => {
    it("reveals a its number of border mines", () => {
      return true;
    });
  });

  describe("Flag click", () => {
    it("can be flagged with 'alt/option + click'", () => {
      return true;
    });

    it("can remove a flag", () => {
      return true;
    });
  });

  it("is no longer 'active' after being clicked", () => {
    cy.visit("/");
    cy.get(".cell-container")
      .first()
      .click()
      .should("not.have.class", "active");
  });
});
