import Cell from "@/components/Cell.vue";

export default {
  name: "Board",
  data: () => {
    return {
      title: "Minesweeper",
      board: [],
      rows: 5,
      cols: 5,
      mines: 5,
      safeCells: 20,
    };
  },

  mounted() {
    this.generateBoard(this.rows, this.cols, this.mines);
  },

  methods: {
    randomNum: max => {
      // return a random number between 0 and max inclusively
      return Math.floor(Math.random() * Math.floor(max));
    },

    generateBoard(rows = this.rows, cols = this.cols, mines = this.mines) {
      if(!this.validGame()){ return }

      this.safeCells = rows * cols - mines;

      const getAvailableCells = () => {
        const availableCells = [];
        for (let i = 0; i < rows; i++) {
          for (let j = 0; j < cols; j++) {
            if (this.board[i][j].value === "_") {
              availableCells.push([i, j]);
            }
          }
        }
        return availableCells;
      };

      // Initialize board to entirely empty but active cells
      for (let r = 0; r < rows; r++) {
        this.$set(this.board, r, []);
        for (let c = 0; c < cols; c++) {
          this.$set(this.board[r], c, {
            value: "_",
            active: true
          });
        }
      }

      // Place mines
      for (let m = 0; m < mines; m++) {
        const availableCells = getAvailableCells();
        const randomNum = this.randomNum(availableCells.length);
        const cell = availableCells[randomNum];
        const row = cell[0];
        const col = cell[1];
        this.$set(this.board[row][col], "value", "x");
      }
    },

    handleClick(row, col) {
      // this cell has already been clicked
      if(this.board[row][col].active === false) {
        return false;
      } else {
        this.$set(this.board[row][col], "active", false);
      }

      // Triggered a mine
      if(this.board[row][col].value === 'x') {
        alert('you lose!!')
        this.resetGame()
      } else {
        this.safeCells--;
      }

      // Game won?
      if(this.safeCells === 0) {
        alert('you will!!')
        this.resetGame()
      }

    },

    isGameActive() {
      if(this.safeCells === 0) {
        alert('you will!!')
        this.resetGame()
      }
    },

    resetGame() {
      if(!this.validGame()){ return }
      this.board = [];
      this.generateBoard(this.rows, this.col, this.mines);
    },

    validGame() {
      let errors = [];

      if(this.mines >= this.rows * this.cols){
        errors.push('Error: Mines cannot outnumber Cells.')
      }

      if(this.rows <= 0){
        errors.push('Error: Rows must be greater than zero.')
      }

      if(this.cols <= 0){
        errors.push('Error: Columns must be greater than zero.')
      }

      if(errors.length === 0){
        return true;
      } else {
        errors.forEach(e => {
          console.error(e)
        })
        return false
      }
    }
  },

  components: {
    Cell
  }
};
